INSERT INTO `visagiste_template` (`id`, `name`, `content`, `last_modification`) VALUES 
(1,'content.html','{% extends \"base.html\" %}\r\n{% block structure %}\r\n<table width=\"100%\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n  <colgroup>\r\n    <col width=\"250\"/>\r\n    <col width=\"650\"/>\r\n    <col width=\"auto\"/>\r\n  </colgroup>\r\n  <tr>\r\n    <td valign=\"top\">\r\n      {% include \"catlist.html\" %}\r\n      {% include \"artlist.html\" %}\r\n    </td>\r\n    <td valign=\"top\">\r\n      <div id=\"placeholder\">\r\n	<div class=\"panel\">\r\n	  {% block panel_top %}{% endblock %}\r\n	</div>\r\n	<div id=\"content\">\r\n	  {% block content %}{% endblock %}\r\n	</div>\r\n	<div class=\"panel footer\">\r\n	  {% block panel_bottom %}{% endblock %}\r\n	</div>\r\n      </div>\r\n    </td>\r\n    <td valign=\"top\">\r\n      {% include \"taglist.html\" %}\r\n    </td>\r\n  </tr>\r\n</table>\r\n{% endblock %}\r\n','2009-09-22 18:59:16'),
(2,'404.html','{% extends \"content.html\" %}\r\n\r\n{% load markup %}\r\n\r\n{% block panel_top %}\r\n<div class=\"title\">\r\n  Нет такой страницы\r\n</div>\r\n{% endblock %}\r\n\r\n{% block panel_bottom %}\r\n<div class=\"title\">\r\n  Нет такой страницы\r\n</div>\r\n{% endblock %}\r\n\r\n{% block content %}\r\n<div class=\"title\">Ошибка 404</div>\r\n\r\n<div class=\"warning\">\r\n  <p>\r\n    Запрошенная вами страница не найдена!\r\n  </p>\r\n      \r\n  <p>\r\n    Возможно вам помогут метки или поиск?\r\n  </p>\r\n</div>\r\n{% endblock %}\r\n','2009-09-22 18:59:59'),
(3,'500.html','{% extends \"content.html\" %}\r\n\r\n{% load markup %}\r\n\r\n{% block panel_top %}\r\n<div class=\"title\">\r\n  Произошла ошибка\r\n</div>\r\n{% endblock %}\r\n\r\n{% block panel_bottom %}\r\n<div class=\"title\">\r\n  Произошла ошибка\r\n</div>\r\n{% endblock %}\r\n\r\n{% block content %}\r\n<div class=\"title\">Ошибка 500</div>\r\n\r\n<div class=\"warning\">\r\n  <p>\r\n    В результате обработки запроса произошла страшная ошибка. Мы\r\n    немедленно отправили сообщение об этом администратор и он уже\r\n    полсекунды назад начал разбираться с данной проблемой.\r\n  </p>\r\n      \r\n  <p>\r\n    Возможно вам помогут метки или поиск?\r\n  </p>\r\n</div>\r\n{% endblock %}\r\n','2009-09-22 19:00:57'),
(4,'artlist.html','<div id=\"categories\">\r\n  <div class=\"title\">\r\n    Статьи\r\n  </div>\r\n  {% for art in article_list %}\r\n  <div class=\"item {% ifequal this.url art.url %} choosen{% endifequal %}\" title=\"{{ art.title }}\">\r\n    <a href=\"{{ art.get_absolute_url }}\"><div class=\"overflow\">{{ art.title }}</div></a>\r\n  </div>\r\n  {% empty %}\r\n  <div class=\"empty\">\r\n    Надо обязательно заполнить этот раздел!\r\n  </div>\r\n  {% endfor %}\r\n</div>\r\n','2009-09-22 19:01:22'),
(5,'base.html','<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Strict//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd\">\r\n{% load i18n %}\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"ru\" lang=\"ru\">\r\n<head>\r\n  <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\"/>\r\n  <meta http-equiv=\"Content-Language\" content=\"ru\"/>\r\n  <meta name=\"ROBOTS\" content=\"ALL\"/>\r\n  <meta name=\"generator\" content=\"Django\"/>\r\n  <title>{{ page_title }}</title>\r\n  <link rel=\"stylesheet\" href=\"http://moiluchru/css/shop.css\" type=\"text/css\"/>\r\n  <link rel=\"shortcut icon\" href=\"http://moiluchru/pics/favicon.png\"/>\r\n  <script type=\"text/javascript\" src=\"http://moiluchru/js/jquery.js\"></script>\r\n  <script type=\"text/javascript\" src=\"http://moiluchru/js/jquery.hotkeys.js\"></script>\r\n  <script type=\"text/javascript\" src=\"http://moiluchru/js/shop.js\"></script>\r\n</head>\r\n\r\n<body>\r\n  <div id=\"opaco\" class=\"hide\"></div> {# элемент для затемнения экрана #}\r\n  <div id=\"popup\" class=\"hide\"></div> {# элемент для всплывающих окон #}\r\n\r\n  {% include \"header.html\" %}\r\n  {% block structure %}{% endblock %}\r\n  {% include \"footer.html\" %}\r\n  {% include \"jabber.html\" %}\r\n\r\n  <div id=\"advicebot\">\r\n    <div class=\"trigger\"></div>\r\n    <table class=\"hide\">\r\n      <tr>\r\n	<td class=\"title\">Обновите браузер</td>\r\n	<td class=\"desc\">\r\n	  Для полноценного использования данного сайта и множества\r\n	  других, рекомендуем Вам обновить Ваш браузер. Хорошим\r\n	  вариантом будет использование <a\r\n	  href=\"http://www.mozilla.com/\">Firefox 3+</a>.\r\n	</td>\r\n      </tr>\r\n    </table>\r\n  </div>\r\n</body>\r\n\r\n{% if not debug %}{{ settings.GOOGLE_ANALYTICS|safe }}{% endif %}\r\n</html>\r\n','2009-09-22 19:01:46'),
(6,'cart.html','{% extends \"content.html\" %}\r\n{% load thumbnail_filter %}\r\n\r\n{% block panel_top %}\r\n<div class=\"rfloat\"><a href=\"/order/\">Оформить заказ</a></div>\r\n<div class=\"title lfloat\">Корзина</div>\r\n{% endblock %}\r\n\r\n{% block panel_bottom %}\r\n<div class=\"rfloat\"><a href=\"/order/\">Оформить заказ</a></div>\r\n<div class=\"title lfloat\">Корзина</div>\r\n{% endblock %}\r\n\r\n{% block content %}\r\n<script>\r\n  function cart_remove_item(item_id) {\r\n      thumbnail = $(\'#th_\' + item_id)\r\n      $(\'<img id=\"loading\" class=\"hide\" src=\"http://moiluchru/pics/loading.gif\" title=\"Ожидайте...\" border=\"0\"/>\')\r\n      .insertAfter(thumbnail).css({\'position\': \'relative\', \'top\': \'-\'+thumbnail.height()/2+\'px\'})\r\n      .toggleClass(\'hide\');\r\n\r\n      $.post(\'/ajax/cart/remove/\', { item: item_id },\r\n 	     function(json) {\r\n		 var code = json[\'code\'];\r\n		 var count = json[\'cart_count\'];\r\n		 var price = json[\'cart_price\'];\r\n		 if (code == 200) {\r\n		     if (count > 0) {\r\n			 // обновить корзину\r\n			 update_cart(count, price);\r\n			 // обновить общую сумму\r\n			 $(\'#cart_total\').html(price);\r\n			 // удалить строку с товаром с интерфейса\r\n			 $(\'tr[item=\' + item_id + \']\').remove();\r\n		     } else {\r\n			 document.location = \'/cart/\';\r\n		     }\r\n		 } else {\r\n		     alert(code + \': \' + json[\'desc\']);\r\n		 }\r\n	     }, \'json\');\r\n  }\r\n  function cart_recalculate(input) {\r\n      id = $(input).attr(\'item\')\r\n      $.post(\'/ajax/cart/recalculate/\', \r\n	     { item: id, count: $(input).attr(\'value\') }, \r\n 	     function(json) {\r\n		 if (json[\'code\'] == 200) {\r\n		     // обновить корзину\r\n		     update_cart(json[\'cart_count\'], json[\'cart_price\']);\r\n		     // обновить общую сумму\r\n		     $(\'#cart_total\').html(json[\'cart_price\']);\r\n		     $(\'#total_\' + id).html(json[\'item_total\']);\r\n		 } else {\r\n		     alert(json[\'code\'] + \': \' + json[\'desc\']);\r\n		 }\r\n	     }, \'json\');\r\n  }\r\n</script>\r\n\r\n<table id=\"cart-list\" width=\"100%\" class=\"cart\" cellpadding=\"4\">\r\n  <colgroup>\r\n    <col width=\"100\"/>\r\n    <col width=\"30%\"/>\r\n    <col width=\"20%\"/>\r\n    <col width=\"20%\"/>\r\n    <col width=\"20%\"/>\r\n    <col width=\"50\"/>\r\n  </colgroup>\r\n  <tr class=\"header\">\r\n    <th class=\"left\">Товар</td>\r\n    <th class=\"left\">Наименование</td>\r\n    <th class=\"center\">Цена</td>\r\n    <th class=\"center\">Кол-во</td>\r\n    <th class=\"center\">Стоимость</td>\r\n    <th></td>\r\n  </tr>\r\n\r\n  {% for i in items %}\r\n  <tr item=\"{{ i.record.id }}\">\r\n    <td height=\"100\" align=\"center\" valign=\"middle\" class=\"thumbnail-containter\">\r\n      <a href=\"{{ i.record.get_absolute_url }}\">\r\n	<img id=\"th_{{ i.record.id }}\" src=\"{{ i.record.image.path|thumbnail:\"100x,itempics\" }}\" \r\n             border=\"0\" title=\"{{ i.record.title }}: Перейти на страницу товара\"/></a>\r\n    </td>\r\n    <td class=\"longitem\" title=\"{{ i.title }}\">\r\n      <div style=\"font-size: 150%;\">{{ i.record.title }}</div>\r\n      <div class=\"overflow\">\r\n	<a href=\"{{ i.record.category.get_absolute_url }}\" \r\n	   title=\"Просмотреть товары этой категории\">{{ i.record.category }}</a>\r\n      </div>\r\n      {% if i.record.collection %}\r\n      <div>\r\n	<a href=\"{{ i.record.collection.get_absolute_url }}\" \r\n	   title=\"Просмотреть товары этой коллекции\">Коллекция</a>\r\n      </div>\r\n      {% endif %}\r\n    </td>\r\n    <td class=\"right nowrap\"><span class=\"money\">{{ i.price|floatformat:2 }}</span> руб</td>\r\n    <td class=\"center mono\">\r\n      <input type=\"text\" name=\"count_{{ i.record.id }}\" class=\"active-zone\"\r\n	     title=\"Пересчёт происходит после покидания поля. Совет: нажмите [TAB]\"\r\n             onchange=\"cart_recalculate(this);\" item=\"{{ i.record.id }}\"\r\n	     value=\"{{ i.count }}\" style=\"width: 30px; text-align: right;\"/>\r\n    </td>\r\n    <td class=\"right nowrap\"><span id=\"total_{{ i.record.id }}\" class=\"money\">{{ i.cost|floatformat:2 }}</span> руб.</td>\r\n    <td><span class=\"button\" \r\n	      title=\"Удалить товар из корзины\"\r\n	      onclick=\"cart_remove_item({{ i.record.id }});\">x</span></td>\r\n  </tr>\r\n  {% comment %}\r\n  {% if not forloop.last %}\r\n  <tr><td colspan=\"6\" style=\"border-top: 1px dashed gray\">&nbsp;</td></tr>\r\n  {% endif %}\r\n  {% endcomment %}\r\n  {% empty %}\r\n  <tr item=\"None\">\r\n    <td height=\"100\" align=\"center\" valign=\"middle\" class=\"thumbnail-containter\"></td>\r\n    <td class=\"longitem\" title=\"{{ i.title }}\">\r\n      <div style=\"font-size: 150%;\">Нет товаров</div>\r\n    </td>\r\n    <td class=\"right nowrap\"><span class=\"money\">0.00</span> руб</td>\r\n    <td class=\"center mono\">\r\n      <input type=\"text\" name=\"count_None\" value=\"0\" style=\"width: 30px; text-align: right;\"/>\r\n    </td>\r\n    <td class=\"right nowrap\"><span id=\"total_None\" class=\"money\">0.00</span> руб.</td>\r\n    <td class=\"right\"></td>\r\n  </tr>\r\n  {% endfor %}\r\n\r\n  <tr class=\"total\">\r\n    <td colspan=\"6\" class=\"right\">\r\n      <span class=\"nowrap\">\r\n	Итого <span id=\"cart_total\" class=\"money\">{{ cart_price|floatformat:2 }}</span> руб.\r\n      </span>\r\n  </tr>\r\n</table>\r\n\r\n{% if cart %}\r\n<br/>\r\n<div class=\"right\">\r\n  <button id=\"offer_apply\" onclick=\"document.location=\'/offer/\';\">Оформить заказ</button>\r\n  <button id=\"offer_cancel\" \r\n	  onclick=\"clean_cart(\'/shop/\'); $(\'offer_apply\').disabled=$(\'offer_cancel\').disabled=true; document.location=\'/\';\">Отменить заказ</button>\r\n</div>\r\n{% endif %}\r\n{% endblock %}\r\n','2009-09-22 19:02:25'),
(7,'catlist.html','<div id=\"categories\">\r\n  <div class=\"title\">\r\n    Категории\r\n  </div>\r\n  <div class=\"item {% ifequal this.slug \'popular\' %} choosen{% endifequal %}\" title=\"Популярные\">\r\n    <a href=\"/category/popular/\"><div class=\"overflow\">Популярные</div></a>\r\n  </div>\r\n  <div class=\"item {% ifequal this.slug \'new\' %} choosen{% endifequal %}\" title=\"Новинки\">\r\n    <a href=\"/category/new/\"><div class=\"overflow\">Новинки</div></a>\r\n  </div>\r\n  {% ifequal cat_mode \'search\' %}\r\n  <div class=\"item choosen\" title=\"Результаты поиска\">\r\n    <div class=\"overflow\">Результаты поиска</div>\r\n  </div>\r\n  {% endifequal %}\r\n  {% for cat in category_list %}\r\n  <div class=\"item {% ifequal this.slug cat.slug %} choosen{% endifequal %}\" title=\"{{ cat.title }}\">\r\n    <a href=\"{{ cat.get_absolute_url }}\"><div class=\"overflow\">{{ cat.title }}</div></a>\r\n  </div>\r\n  {% endfor %}\r\n</div>\r\n','2009-09-22 19:02:46'),
(8,'flatpage.html','{% extends \"content.html\" %}\r\n\r\n{% load markup %}\r\n\r\n{% block panel_top %}\r\n<div class=\"title\">\r\n  {{ title }}\r\n</div>\r\n{% endblock %}\r\n\r\n{% block panel_bottom %}\r\n<div class=\"title\">\r\n  {{ title }}\r\n</div>\r\n{% endblock %}\r\n\r\n{% block content %}\r\n<div class=\"text\">\r\n  {{ body|markdown }}\r\n</div>\r\n{% endblock %}\r\n','2009-09-22 19:03:09'),
(9,'footer.html','<table style=\" background-color: #FFEB73; margin-top: 1em;\">\r\n  <colgroup>\r\n    <col width=\"200\"/>\r\n    <col width=\"auto\"/>\r\n  </colgroup>\r\n  <tr>\r\n    <td	style=\"color: white; background-color: #bfab30; padding: 1em; text-align: center;\">\r\n      Powered by <a href=\"http://djbook.ru/\">Django</a>\r\n    </td>\r\n    <td style=\"padding-left: 3em; text-align: right; padding: 1em; \">\r\n      <a href=\"mailto:info@moiluch.ru\">@ Информация</a>\r\n      <a href=\"mailto:support@moiluch.ru\">@ Поддержка</a>\r\n    </td>\r\n  </tr>\r\n</table>\r\n','2009-09-22 19:03:31'),
(10,'header.html','{# header.html begins #}\r\n<table id=\"control\" width=\"100%\">\r\n  <colgroup>\r\n    <col width=\"250\"/>\r\n    <col width=\"auto\"/>\r\n    <col width=\"250\"/>\r\n  </colgroup>\r\n  <tr>\r\n    <td valign=\"middle\">\r\n      <div style=\"width: 220px; margin: 0px 8px; padding: 8px;\"\r\n	   onclick=\"document.location=\'/\';\" class=\"pointer\">\r\n	<div style=\"font-family: comic sans ms; font-size: 300%; padding-top: 10px;\">{{ settings.SITE_TITLE }}</div>\r\n	<div style=\"font-family: comic sans ms; font-size: 120%; color: orange; position: relative; top: -18px; text-align: center;\">{{ settings.SITE_SUBTITLE }}</div>\r\n      </div>\r\n      {# <span><a href=\"/contact/\">Контакты</a></span> #}\r\n    </td>\r\n\r\n    <td valign=\"bottom\" style=\"text-align: center; padding: 4px 8px;\">\r\n      <div id=\"title-phone\">\r\n	<span class=\"region-code\">({{ settings.SITE_PHONE_PREFIX }})</span>\r\n	<img src=\"http://moiluchru/pics/phone.png\" alt=\"\" title=\"\"/>\r\n	<span class=\"phone-number\">{{ settings.SITE_PHONE_SUFFIX }}</span>\r\n      </div>\r\n      <form method=\"post\" action=\"/result/\">\r\n	<div>\r\n	  <input type=\"text\" name=\"userinput\" value=\"{{ search_query }}\"/>\r\n	  <input type=\"hidden\" name=\"howmuch\" value=\"1\"/>\r\n	</div>\r\n      </form>\r\n      <div style=\"text-align: right;\"><a href=\"/search/\">Расширенный поиск</a></div>\r\n    </td>\r\n\r\n    <td valign=\"middle\">\r\n      <div id=\"cartwidget\">\r\n	<div class=\"header\">\r\n	  <span title=\"Очистить корзину\" style=\"font-family: arial; font-size: 80%; float: right; vertical-align: middle; \"><a href=\"#\" onclick=\"clean_cart(); return false;\">Очистить</a></span>\r\n	  КОРЗИНА\r\n	</div>\r\n	<div class=\"body\">\r\n	  <div>\r\n	    <span class=\"rfloat\"><span id=\"cart_price\" class=\"price\">{{ cart_price|floatformat:2 }}</span> руб</span>\r\n	    <span class=\"lfloat\">Цена:</span>\r\n	    <div style=\"clear: both;\"></div>\r\n	  </div>\r\n	  <div>\r\n	    <span class=\"rfloat\"><span id=\"cart_count\" class=\"price\">{{ cart_count }}</span></span>\r\n	    <span class=\"lfloat\">Количество:</span>\r\n	  </div>\r\n	  <div style=\"clear: both;\"></div>\r\n	</div>\r\n	<div class=\"footer\">\r\n	  <span style=\"float: right;\"><a href=\"/delivery/\">Доставка</a></span>\r\n	  <span><a href=\"/cart/\">Оформить заказ</a></span>\r\n	</div>\r\n      </div>\r\n    </td>\r\n  </tr>\r\n</table>\r\n{# header.html ends #}\r\n','2009-09-22 19:03:51'),
(11,'item.html','{% extends \"content.html\" %}\r\n\r\n{% load thumbnail_filter %}\r\n{% load markup %}\r\n\r\n{% block panel_top %}\r\n<div class=\"rfloat\">\r\n  {% if previous %}\r\n  <a href=\"{{ previous.get_absolute_url }}\"><span title=\"Предыдущий товар категории\" class=\"button\">&lt;</span></a>\r\n  {% else %}\r\n  <span title=\"Предыдущий товар категории\" class=\"button disabled\">&lt;</span>\r\n  {% endif %}\r\n\r\n  {% if next %}\r\n  <a href=\"{{ next.get_absolute_url }}\"><span title=\"Следующий товар категории\" class=\"button\">&gt;</span></a>\r\n  {% else %}\r\n  <span title=\"Следующий товар категории\" class=\"button disabled\">&gt;</span>\r\n  {% endif %}\r\n</div>\r\n\r\n<div class=\"title lfloat\">\r\n  Артикул: {{ item.title }}\r\n</div>\r\n{% endblock %}\r\n\r\n{% block panel_bottom %}\r\n<div class=\"rfloat\">\r\n  {% if previous %}\r\n  <a href=\"{{ previous.get_absolute_url }}\"><span title=\"Предыдущий товар категории\" class=\"button\">&lt;</span></a>\r\n  {% else %}\r\n  <span title=\"Предыдущий товар категории\" class=\"button disabled\">&lt;</span>\r\n  {% endif %}\r\n\r\n  {% if next %}\r\n  <a href=\"{{ next.get_absolute_url }}\"><span title=\"Следующий товар категории\" class=\"button\">&gt;</span></a>\r\n  {% else %}\r\n  <span title=\"Следующий товар категории\" class=\"button disabled\">&gt;</span>\r\n  {% endif %}\r\n</div>\r\n\r\n<div class=\"title lfloat\">\r\n  Артикул: {{ item.title }}\r\n</div>\r\n{% endblock %}\r\n\r\n{% block content %}\r\n<table width=\"100%\" height=\"210\" border=\"0\" cellspacing=\"4\">\r\n  <colgroup>\r\n    <col width=\"200\"/>\r\n    <col width=\"auto\"/>\r\n    <col width=\"auto\"/>\r\n  </colgroup>\r\n  <tr>\r\n    <td height=\"200\" align=\"center\" valign=\"middle\" id=\"image-container\"\r\n	onclick=\"$(\'#cartadd_{{ item.id }}\').toggleZoom(); return false;\">\r\n      {# Изображение товара #}\r\n      <img src=\"{{ item.image.path|thumbnail:\"200x,itempics\" }}\" id=\"thumbnail\" border=\"0\" title=\"{{ item.title }} : Нажмите, чтобы увеличить\" />\r\n	{% for l in lamp %}\r\n	<img id=\"lamp_{{ l.socle|lower|cut:\' \' }}\" src=\"http://moiluchru/pics/lamps/{{ l.socle|lower|cut:\' \' }}.jpeg\" class=\"hide\" \r\n	     style=\"position: relative; top: 0px; left: 0px;\"/>\r\n	{% endfor %}\r\n    </td>\r\n\r\n    <td style=\"padding: 0px 4px; border: 0px dotted gray; border-width: 0px 1px 0px 1px;\">\r\n      <div id=\"addwidget\" class=\"active-zone\"\r\n	     onclick=\"$(\'#cartadd_{{ item.id }}\').toggleZoom(); return false;\">\r\n\r\n	<div class=\"important-zone-discount\"\r\n	     title=\"При покупке нескольких элементов коллекции спрашивайте о скидке.\">\r\n	    Скидка?\r\n	</div>\r\n\r\n	<img src=\"http://moiluchru/pics/add.png\" title=\"Добавить в корзину\" width=\"32\" height=\"32\" align=\"left\" style=\"margin: 0.5em; 4px;\"/>\r\n	<div style=\"line-height: 1.0;\">\r\n	  <div style=\"color: #2c3d82;\">Цена:</div>\r\n	  <span style=\"font-size: 200%; font-weight: bold; color: #071c71;\">{{ item.get_price.1|floatformat:2 }}</span>\r\n	  <span style=\"color: #2c3d82;\">руб.</span>\r\n	</div>\r\n\r\n	<div style=\"text-align: center; text-decoration: underline;\">\r\n	  Добавить в корзину\r\n	</div>\r\n      </div>\r\n      <div>{% ifequal item.is_present 1 %}Есть в наличии{% else %}Ожидается{% endifequal %}.</div>\r\n\r\n	{% if jabber %}\r\n	<div onclick=\"jabber_init();\"\r\n	     style=\"border: 1px solid green; -moz-border-radius: 4px; \r\n		    padding: 4px; margin: 4px; cursor: pointer; text-align: center;\">\r\n	  <img src=\"http://moiluchru/pics/jabber-protocol.png\" align=\"left\"/> Задать вопрос консультанту\r\n	</div>\r\n	<br/>\r\n	{% endif %}\r\n    </td>\r\n      \r\n    <td valign=\"top\" style=\"padding: 4px;\">\r\n      <h4>Характеристики</h4>\r\n      <div>\r\n	<div>Цвет: <span>{{ item.color }}</span></div>\r\n	{% if addons.diameter %}<div>Диаметр: {{ addons.diameter }} мм.</div>{% endif %}\r\n	{% if addons.height %}<div>Высота: {{ addons.height }} мм.</div>{% endif %}\r\n	{% if addons.length %}<div>Длина: {{ addons.length }} мм.</div>{% endif %}\r\n	{% if addons.width %}<div>Ширина: {{ addons.width }} мм.</div>{% endif %}\r\n	{% if addons.brow %}<div>Выступ: {{ addons.brow }} мм.</div>{% endif %}\r\n      </div>\r\n\r\n      {% if lamp %}\r\n      <h4 class=\"ttl\"><span></span>Лампочки</h4>\r\n      {% for l in lamp %}\r\n      <div style=\"padding: 2px;\">\r\n	<span class=\"active-zone\"\r\n	      onmouseover=\"show_lamp(\'lamp_{{ l.socle|lower|cut:\' \' }}\');\" \r\n	      style=\"font-size: 150%; font-weight: bold;\">{{ l.socle }}</span>\r\n	<span style=\"font-size: 80%;\">{{ l.count }} x {{ l.watt }} Вт</span>\r\n      </div>\r\n      <img id=\"lamp_{{ l.socle|lower }}\" src=\"/pics/lamps/{{ l.socle|lower }}.jpeg\" class=\"hide\" style=\"border: 2px solid red; -moz-border-radius: 8px; padding: 4px; background-color: white;\"/>\r\n      {% endfor %}\r\n      <div>{% ifequal item.has_lamp 1 %}Лампочки идут в поставке{% else %}Лампочек в поставке нет{% endifequal %}.</div>\r\n      {% endif %}\r\n\r\n    </td>\r\n  </tr>\r\n</table>\r\n\r\n{% if item.desc %}\r\n<div>\r\n  <div class=\"title\">Описание</div>\r\n  <div class=\"desc\">{{ item.desc|markdown }}</div>\r\n  <div class=\"desc\">Приоритетная доставка &mdash; Москва, Красногорск, Нахабино, Дедовск, Снегири, Истра.</div>\r\n</div>\r\n{% endif %}\r\n\r\n{# Отображение остальных элементов коллекции #}\r\n{% if collection %}\r\n<div>\r\n  <div class=\"title\">Коллекция</div>\r\n\r\n  <div>\r\n    {% for i in collection %}\r\n    <table width=\"100\" height=\"130\" class=\"collection-list-item\">\r\n      <tr>\r\n	<td align=\"center\" valign=\"middle\">\r\n	  <a href=\"{{ i.get_absolute_url }}\" title=\"Нажмите для просмотра товара\">{{ i.title }}</a>\r\n	</td>\r\n      </tr>\r\n      <tr>\r\n	<td align=\"center\" valign=\"middle\" height=\"100\" width=\"100\" >\r\n	  <a href=\"{{ i.get_absolute_url }}\" title=\"Нажмите для просмотра товара\"><img src=\"{{ i.image.path|thumbnail:\"100x,itempics\" }}\" border=\"0\"/></a>\r\n	</td>\r\n      </tr>\r\n    </table>\r\n    {% endfor %}\r\n  </div>\r\n\r\n  <div style=\"clear: both;\"></div>\r\n</div>\r\n{% endif %}\r\n\r\n{% include \"zoom.html\" %}\r\n{% endblock %}\r\n','2009-09-22 19:04:15'),
(12,'jabber.html','{# jabber client - begin #}\r\n<div id=\"jabber-client\" class=\"hide\">\r\n  <div class=\"title\">Онлайн консультация</div>\r\n  <img src=\"/pics/loading.gif\" id=\"loading\" style=\"position: fixed; z-index: 2;\" class=\"hide\"/>\r\n  <div id=\"chat-window\">\r\n    <div style=\"padding: 4px; border: 1px solid orange; -moz-border-radius: 4px; background-color: #ffffaa;\">\r\n      Для отправки набранного сообщения просто нажмите [Enter]. Для прекращения сеанса &mdash; нажмите [Esc].\r\n    </div>\r\n    <div id=\"down\"></div>\r\n  </div>\r\n  <div id=\"message-window\">\r\n    <input type=\"text\" maxlength=\"1024\" value=\"\"/>\r\n  </div>\r\n</div>\r\n{# jabber client - end #}\r\n','2009-09-22 19:04:33'),
(13,'list.html','{% extends \"content.html\" %}\r\n\r\n{% block panel_top %}\r\n  {% include \"paginator.html\" %}\r\n{% endblock %}\r\n\r\n{% block panel_bottom %}\r\n  {% include \"paginator.html\" %}\r\n{% endblock %}\r\n\r\n{% block content %}\r\n<table width=\"100%\">\r\n  <tr>\r\n    {% for column in column_list %}\r\n      <td valign=\"top\" width=\"50%\">\r\n        {% for item in column %}\r\n          {% include \"short.html\" %}\r\n        {% empty %}\r\n          К сожалению база товаров на данный момент пуста.\r\n        {% endfor %}\r\n      </td>\r\n      {% if not forloop.last %}\r\n        <td width=\"8em\">&nbsp;</td>\r\n      {% endif %}\r\n    {% endfor %}\r\n  </tr>\r\n</table>\r\n{% endblock %}\r\n','2009-09-22 19:04:57'),
(14,'order.html','{% extends \"content.html\" %}\r\n\r\n{% block panel_top %}\r\n<div class=\"title\">\r\n  Оформление заказа\r\n</div>\r\n{% endblock %}\r\n\r\n{% block panel_bottom %}\r\n<div class=\"title\">\r\n  Оформление заказа\r\n</div>\r\n{% endblock %}\r\n\r\n{% block content %}\r\n<div class=\"title\">Информация о покупателе</div>\r\n\r\n<div class=\"sectioncontent\">\r\n  <form id=\"customer\" method=\"post\" action=\"\">\r\n    <table class=\"container\" celpadding=\"0\" cellspacing=\"0\">\r\n      {{ form }}\r\n    </table>\r\n    \r\n    <div class=\"right\">\r\n      <input type=\"submit\" id=\"order_submit\" value=\"Подтвердить заказ\"/>\r\n      <input type=\"reset\" id=\"order_cancel\"\r\n	     onclick=\"clean_cart(\'/\'); $(\'order_submit\').disabled=$(\'order_cancel\').disabled=true; document.location=\'/\';\" \r\n	     value=\"Отменить заказ\"/>\r\n    </div>\r\n  </form>\r\n</div>\r\n{% endblock %}\r\n','2009-09-22 19:05:25'),
(15,'paginator.html','{% if page %}\r\n<div class=\"paginator\">\r\n  <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n  <colgroup>\r\n    <col width=\"auto\"/>\r\n    <col width=\"auto\"/>\r\n    <col width=\"auto\"/>\r\n  </colgroup>\r\n    <tr>\r\n      <td align=\"left\">\r\n	Страница {{ page.number }} из {{ page.paginator.num_pages }}\r\n\r\n	{% if page.previous_ten %}\r\n	<a href=\"{{ url }}{{ page.previous_ten }}/\">\r\n	  <span title=\"Назад на 10 страниц\" class=\"button\">&lt;&lt;&lt;</span></a>\r\n	{% else %}\r\n	<span title=\"Назад на 10 страниц\" class=\"button disabled\">&lt;&lt;&lt;</span>\r\n	{% endif %}\r\n\r\n	{% if page.has_previous %}\r\n	<a href=\"{{ url }}{{ page.previous_page_number }}/\">\r\n	  <span title=\"Предыдущая страница\" class=\"button\">&lt;</span></a>\r\n	{% else %}\r\n	<span title=\"Предыдущая страница\" class=\"button disabled\">&lt;</span>\r\n	{% endif %}\r\n\r\n	{% if page.has_next %}\r\n	<a href=\"{{ url }}{{ page.next_page_number }}/\">\r\n	  <span title=\"Следующая страница\" class=\"button\">&gt;</span></a>\r\n	{% else %}\r\n	<span title=\"Следующая страница\" class=\"button disabled\">&gt;</span>\r\n	{% endif %}\r\n\r\n	{% if page.next_ten %}\r\n	<a href=\"{{ url }}{{ page.next_ten }}/\">\r\n	  <span title=\"Вперёд на 10 страниц\" class=\"button\">&gt;&gt;&gt;</span></a>\r\n	{% else %}\r\n	<span title=\"Вперёд на 10 страниц\" class=\"button disabled\">&gt;&gt;&gt;</span>\r\n	{% endif %}\r\n      </td>\r\n      <td>&nbsp;</td>\r\n      <td align=\"right\" class=\"nowrap\">\r\n	<span title=\"Сортировка\" class=\"pointer\" onclick=\"document.location=\'/sort/1/\';\">Популярность\r\n	  {% ifequal sort_type 1 %}<img src=\"http://moiluchru/pics/sort-down.png\"/>{% endifequal %}\r\n	  {% ifequal sort_type 2 %}<img src=\"http://moiluchru/pics/sort-up.png\"/>{% endifequal %}\r\n	</span>\r\n	<span style=\"margin: 0px 1em;\">&nbsp;</span>\r\n	<span title=\"Сортировка\" class=\"pointer\" onclick=\"document.location=\'/sort/2/\';\">Цена\r\n	  {% ifequal sort_type 3 %}<img src=\"http://moiluchru/pics/sort-down.png\"/>{% endifequal %}\r\n	  {% ifequal sort_type 4 %}<img src=\"http://moiluchru/pics/sort-up.png\"/>{% endifequal %}\r\n	</span>\r\n      </td>\r\n    </tr>\r\n  </table>\r\n</div>\r\n{% endif %}\r\n','2009-09-22 19:05:45'),
(16,'profit.html','{% extends \"content.html\" %}\r\n\r\n{% block panel_top %}\r\n<div class=\"title\">\r\n  Результат\r\n</div>\r\n{% endblock %}\r\n\r\n{% block panel_bottom %}\r\n<div class=\"title\">\r\n  Результат\r\n</div>\r\n{% endblock %}\r\n\r\n{% block content %}\r\n<div class=\"title\">Заказ принят</div>\r\n\r\n<div>\r\n  <p>\r\n    Наш менеджер свяжется с Вами в ближайшее время для подверждения\r\n    Вашего заказа и для уточнения наилучшего времени доставки заказа.\r\n  </p>\r\n\r\n  <p>\r\n    Спасибо, что выбрали нас.\r\n  </p>\r\n</div>\r\n{% endblock %}\r\n','2009-09-22 19:06:01'),
(17,'search.html','{% extends \"content.html\" %}\r\n\r\n{% block panel_top %}\r\n<div class=\"title\">\r\n  Расширенный поиск\r\n</div>\r\n{% endblock %}\r\n\r\n{% block panel_bottom %}\r\n<div class=\"title\">\r\n  Расширенный поиск\r\n</div>\r\n{% endblock %}\r\n\r\n{% block content %}\r\n<script>\r\n  function add_tag_to_list(select, list) {\r\n      var s_obj = $(\'#\'+select)\r\n      var l_obj = $(\'#\'+list)\r\n      var options = s_obj[0].options;\r\n      var selected = s_obj[0].selectedIndex;\r\n      l_obj.val(l_obj.val() + options[selected].value + \' \');\r\n      }\r\n</script>\r\n<div class=\"title\">Рекомендации</div>\r\n<div class=\"desc\">\r\n  <p>\r\n    Если вы не желаете указывать диапазон, например, цены, то не\r\n    вносите изменения в эти поля. Ноль в обоих полях означает, что\r\n    данное условие не будет учитываться при выполнении поиска.\r\n  </p>\r\n</div>\r\n\r\n{% if error_desc %}<div id=\"error\">{{ error_desc }}</div>{% endif %}\r\n    \r\n<form id=\"advanced_search\" method=\"post\" action=\"/result/\">\r\n  <h2>Форма поиска</h2>\r\n\r\n  <p align=\"center\">\r\n    {{ searchform.userinput }}\r\n    <input type=\"hidden\" name=\"howmuch\" value=\"1\"/>\r\n  </p>\r\n  \r\n  <h3>Основные характеристики продукта</h3>\r\n      \r\n  <table id=\"simple_search\" width=\"100%\">\r\n    {{ mainsearchform }}\r\n    {{ sizesearchform }}\r\n  </table>\r\n\r\n  <h3>Уникальные характеристики продукта</h3>\r\n  \r\n  <table id=\"full_search\" width=\"100%\">\r\n    {{ fullsearchform }}\r\n  </table>\r\n  \r\n  <p align=\"right\">\r\n    <input type=\"submit\" name=\"go\" value=\"Найти\"/>\r\n  </p>\r\n</form>\r\n    \r\n{% endblock %}\r\n','2009-09-22 19:08:09'),
(18,'short.html','{% load thumbnail_filter %}\r\n\r\n<table width=\"100%\" height=\"100\" border=\"0\" style=\"margin: 0.8em;\">\r\n  <colgroup>\r\n    <col width=\"100\"/>\r\n    <col width=\"auto\"/>\r\n  </colgroup>\r\n  <tr>\r\n    <td class=\"thumbnail-containter\" onclick=\"$(\'#cartadd_{{ item.id }}\').toggleZoom(); return false;\">\r\n      <img src=\"{{ item.image.path|thumbnail:\"100x,itempics\" }}\" \r\n	   id=\"thumbnail\" border=\"0\" \r\n	   title=\"{{ item.title }}: Нажмите, чтобы увеличить\"/>\r\n    </td>\r\n    <td align=\"left\">\r\n      <div style=\"font-size: 130%;\">Артикул: {{ item.title }}</div>\r\n      <div>\r\n	Категория:\r\n	<a href=\"{{ item.category.get_absolute_url }}\" style=\"white-space: nowrap;\"\r\n	   title=\"Просмотреть товары этой категории\">{{ item.category.title }}</a>\r\n      </div>\r\n      <div>{% ifequal item.is_present 1 %}Есть в наличии{% else %}Ожидается{% endifequal %}</div>\r\n      {% if item.collection %}\r\n      <div>\r\n	<a href=\"{{ item.collection.get_absolute_url }}\" title=\"Перейти к товарам коллекции\">Коллекция</a>\r\n      </div>\r\n      {% endif %}\r\n      <div>Цвет: <span>{{ item.color }}</span></div>\r\n      <div style=\"font-weight: bold;\">Цена: <span>{{ item.get_price.1|floatformat:2 }}</span> руб.</div>\r\n      <a href=\"{{ item.get_absolute_url }}\" title=\"Перейти на страницу товара\">Описание...</a>\r\n    </td>\r\n  </tr>\r\n</table>\r\n\r\n{% include \"zoom.html\" %}\r\n','2009-09-22 19:08:25'),
(19,'taglist.html','<div id=\"tagcloud\">\r\n  <div class=\"title\">\r\n    Метки\r\n  </div>\r\n  <ul>\r\n  {% for tag in tag_list %}\r\n  <li style=\"font-size: {{ tag.font_size }}%;\">\r\n    <a href=\"/tag/{{ tag }}/\">{{ tag }}</a>\r\n  </li>\r\n  {% endfor %}\r\n  </ul>\r\n</div>\r\n','2009-09-22 19:08:41'),
(20,'zoom.html','{% load thumbnail_filter %}\r\n\r\n<div id=\"cartadd_{{ item.id }}\" class=\"hide\">\r\n  <h3 class=\"ttl\"><span></span>{{ item.title }}</h3>\r\n\r\n  <div style=\"position: absolute; top: 0px; right: 0px; width: 180px; height: 4em; \r\n	      text-align: left; vertical-align: bottom; padding-left: 0.5em;\r\n	      border-bottom: 2px solid #FFDB00; border-left: 2px solid #FFDB00;\r\n	      background-color: #FFEB73; opacity: 0.8;\r\n	      -moz-border-radius: 0px 8px 0px 4px;\">\r\n    <div style=\"color: #2c3d82;\">Цена:</div>\r\n    <div style=\"text-align: right;\">\r\n      <span style=\"font-size: 200%; font-weight: bold; color: #071c71;\">{{ item.get_price.1|floatformat:2 }}</span>\r\n      <span style=\"color: #2c3d82;\">руб.</span>\r\n    </div>\r\n  </div>\r\n\r\n  <table width=\"400\" height=\"400\" class=\"pointer\" \r\n       onclick=\"$(this).toggleZoom(); return false;\">\r\n    <tr>\r\n      <td align=\"center\" valign=\"middle\">\r\n	<img src=\"{{ item.image.path|thumbnail:\"400x,itempics\" }}\" \r\n	     id=\"zoomed\"\r\n	     title=\"{{ item.title }}\" \r\n	     border=\"0\" style=\"vertical-align: middle;\"/>\r\n      </td>\r\n    </tr>\r\n  </table>\r\n\r\n  <a href=\"{{ item.get_absolute_url }}\" title=\"Перейти на страницу товара\">Описание...</a>\r\n  <div style=\"text-align: right;\">\r\n    <img id=\"loading\" class=\"hide\" src=\"/pics/loading.gif\" title=\"Ожидайте...\" border=\"0\"/>\r\n    Количество\r\n    <input type=\"text\" id=\"quantity_{{ item.id }}\" value=\"1\" maxlength=\"2\" title=\"Количество\" style=\"width: 30px; text-align: right;\"/>\r\n    <button onclick=\"buy({{ item.id }}, $(\'#quantity_{{ item.id }}\').val()); return false;\">Добавить в корзину</button>\r\n  </div>\r\n  <div class=\"center\">Нажмите на изображение, чтобы закрыть просмотр.</div>\r\n</div>\r\n','2009-09-22 19:09:00');
